import React from 'react';
import Task from './Task';

const TaskList = (props) => {

  const tasks = props.tasks.map(task => (
    <Task
      key={task.id}
      task={task}
    />
  ))

  return (

    <div className="active">
      <h3>Tasks To Do</h3>
      {tasks}
      <hr />

      <h3>Done Tasks (0)</h3>
    </div>
  );
}

export default TaskList;
