import React from 'react';

const Task = (props) => {

  const { date, id, text, active, finishDate } = props.task;
  const doneTaskTime = new Date(finishDate).toLocaleString()

  return (
    <div>
      <h4>

        <strong>{text}</strong>
        {active ? " - wykonac do :" : " - wykonane dnia :"}
        {active ? <span style={{ color: "darkblue" }}> {date}</span> : <span style={{ color: "green" }}> {doneTaskTime}</span>}

        {active ? <button onClick={() => props.change(id)}>Zostalo zrobione</button> : null}

        <button onClick={() => props.delete(id)}>X</button>

      </h4>
    </div>
  );
}

export default Task;
