import React, { Component } from 'react';
import './AddTask.css';


class AddTask extends Component {
  state = {
    value: '',
    checked: false,
    date: '',
  }

  handleAddTask = e => {
    this.setState({
      value: e.target.value
    })
  }

  handleChangePriority = () => {
    this.setState({
      checked: !this.state.checked
    })
  }

  handleChangeDate = e => {
    this.setState({
      date: e.target.value
    })
  }



  render() {
    const { value, checked } = this.state;
    return (
      <div className="form">

        <input type="text" placeholder="add task" value={value} onChange={this.handleAddTask} />

        <input type="checkbox" id="important" checked={checked} onChange={this.handleChangePriority} />
        <label htmlFor="important">Priority</label>

        <br />

        <label htmlFor="">Until when to do</label>
        <input type="date" onChange={this.handleChangeDate} />

        <br />

        <button>Add task</button>

      </div>
    )
  }
}

export default AddTask;
