import React from 'react';

const Task = (props) => {

  const { date, id, text, active, finishDate } = props.task;

  if (active) {
    return (
      <div>
        <h4>
          <strong>{text}</strong> - do it until :<span style={{ color: "red" }}> {date}</span>
          <button onClick={() => props.change(id)}>Done</button>
          <button onClick={() => props.delete(id)}>X</button>
        </h4>
      </div>
    );
  } else {
    const doneTaskTime = new Date(finishDate).toLocaleString();
    return (
      <div>
        <h4>
          <strong>{text}</strong> - made on :<span style={{ color: "green" }}> {doneTaskTime}</span>
          <button onClick={() => props.delete(id)}>X</button>
        </h4>
      </div>
    )
  }
}

export default Task;
